import styled from 'styled-components';

export const Container = styled.div`
    background-image: url(${props => props.image});
    background-size: 150%;
    background-color: #111;
    background-repeat: no-repeat;
    width: 100vw;
    height: 100vh;
    z-index: 1;
`

export const Gradient = styled.div`
    display: block;
    background: linear-gradient(rgba(0, 17, 17, 1) 0%, 
                                rgba(0, 17, 17, 0) 20%,
                                rgba(0, 17, 17, .9) 60%,
                                rgba(0, 17, 17, 1) 100%);
    width: calc(100vw - 40px);
    height: 55vh;
    z-index: 2;
    position: absolute;
    top: 0;
    left: 0;
    padding: 45vh 20px 0 20px;
`

export const Scroll = styled.span`
    display: block;
    position: relative;
    height: 1.5em;
    width: .75em;
    border: .25em solid #fff;
    border-radius: 1em;
`

export const ScrollWheel = styled.span`
    display: block;
    position: absolute;
    left: 50%;
    background: #fff;
    height: .25em;
    width: .25em;
    top: .6em;
    margin-left: -.25em;
    border-radius: 50%;
    transform-origin: top center;
    backface-visibility: hidden;
    animation: scroll_2 2s ease-out infinite;

    @keyframes scroll_2 {
        0%   { transform: translateY(0); opacity: 1; }
        25%  { opacity: 1; }
        75%  { transform: translateY(.75em); opacity: 0; }
        100% { transform: translateY(0); opacity: 0; }
    }
`
