import styled from 'styled-components';
import { Link } from 'react-router-dom';

export const Container = styled.div`
    background-image: url(${props => props.image});
    background-size: 150%;
    background-color: #111;
    background-repeat: no-repeat;
    width: 100vw;
    height: 100vh;
    z-index: 1;
`

export const Gradient = styled.div`
    display: block;
    background: linear-gradient(rgba(0, 17, 17, 1) 0%, 
                                rgba(0, 17, 17, 0) 20%,
                                rgba(0, 17, 17, .9) 60%,
                                rgba(0, 17, 17, 1) 100%);
    width: calc(100vw - 40px);
    height: 55vh;
    z-index: 2;
    position: absolute;
    top: 0;
    left: 0;
    padding: 45vh 20px 0 20px;
`

export const Name = styled.span`
    display: block;
    font-size: 14px;
    color: rgba(255, 255, 255, .7);
    text-transform: uppercase;
    letter-spacing: 2px;
`

export const Function = styled.span`
    display: block;
    font-weight: 500;
    font-size: 32px;
    color: white;
    transform: translateY(-4px);
`

export const Characteristics = styled.div`
    margin-bottom: 1.5em;
`

export const Characteristic = styled.span`
    color: rgba(255, 255, 255, .7);
    white-space: pre;
    letter-spacing: 1px;
    font-weight: 300;

    &:after {       
        content: '';
        display: inline-block;
        height: 16px;
        width: 2px;
        background-color: white;
        margin-right: 16px;
        position: relative;
        left: 8px;
        transform: translateY(4px);
    }
`

export const Button = styled(Link)`
    background-color: #FF4F00;
    padding: 8px 24px;
    border-radius: 4px;
    color: white;
    box-shadow: 1px 1px 1px #C94001;
    cursor: pointer;
    text-decoration: none;
    position: relative;
    transition: all .2s ease-in-out;

    &:hover, &:active {
        background-color: #C94001;
        transition: all .2s ease-in-out;
    }

    @media screen and (max-width: 768px) {
        margin: 0 auto;
        width: 60vw;
        display: block;
        text-align: center;
        margin-top: 4em;
    }
`

export const Scroll = styled.span`
    display: block;
    position: absolute;
    height: 1.5em;
    width: .75em;
    top: calc(100vh - 3em);
    left: 50%;
    transform: translateX(-50%);
    border: 2px solid #fff;
    border-radius: 1em;
`

export const ScrollWheel = styled.span`
    display: block;
    position: absolute;
    left: 50%;
    background: #fff;
    height: .25em;
    width: .25em;
    top: .3em;
    margin-left: -.125em;
    border-radius: 50%;
    transform-origin: top center;
    backface-visibility: hidden;
    animation: scroll_2 2s ease-out infinite;

    @keyframes scroll_2 {
        0%   { transform: translateY(0); opacity: 1; }
        25%  { opacity: 1; }
        75%  { transform: translateY(.375em); opacity: 0; }
        100% { transform: translateY(0); opacity: 0; }
    }
`
