import styled from 'styled-components';

export const Container = styled.div`
    background-image: url(${props => props.image});
    background-size: 150%;
    background-color: #111;
    background-repeat: no-repeat;
    width: 100vw;
    height: 100vh;
`

export const Gradient = styled.div`
        display: block;
        background: linear-gradient(rgba(255, 17, 17, 1) 0%, 
                                    rgba(255, 17, 17, 0) 20%,
                                    rgba(255, 17, 17, .9) 60%,
                                    rgba(255, 17, 17, 1) 100%);
        width: 100vw;
        height: 100vh;
        z-index: 44;
        position: absolute;
        top: 0;
        left: 0;
        padding-top: 50vh;
`
